INSERT INTO cyoubike.common_problem (id, title, content, jurisdiction, is_visible, create_time) VALUES (30, '身份证丢了如何乘坐飞机？', '<p>常规处理办法：</p><p>　　1、证件丢失后，第一时间拨打110；<br>　　2、拨打110的主要目的不是找回证件，而是请警家蜀黍帮忙从户籍资料中调取你身份证或户口本上的信息，打印出来，并出具证件丢失的证明。<br>　　3、携带公安提供的证明，去找机场公安，开具临时身份证，用于换取登机牌，然后用临时身份证去办理登机牌即可。<br>　　*需要注意的是，临时身份证仅当天有效，所以提前办理没用，必须登记当天办理才行。<br><br><br>虹桥机场：</p><p>　　携带2张一寸照片至机场6楼公安派出所提供你的姓名等基本信息系统里会自动检索出你的身份信息，可以很快捷的办理出临时身份证。<br><br><br>常用电话：</p><p>　　虹桥机场派出所T1：22344180</p><p>　　 虹桥机场派出所T2：22380990</p>', 1, 0, 1567562746386);
