INSERT INTO cyoubike.common_problem (id, title, content, jurisdiction, is_visible, create_time) VALUES (57, '门弄号审批、特殊样式门弄牌审批', '<p>一、办理依据：《上海市门弄号管理办法》第五条、第十一条。<br><br>二、办理机构：统一样式的门弄号牌由公安派出所受理申请，各公安分局审批。特色建筑物需要安装特殊样式门弄号标牌的，由市公安局会同有关部门审核确认。<br><br>三、申请材料目录：派出所接待窗口放置《窗口服务告知单》，对门弄号审批的申办事项勾注了对应的申请材料目录。<br><br>四、办理方式：<br><br> （一）网上咨询：单位和个人可以登录上海公安门户网站咨询行政审批事项。<br><br> （二）窗口办理：申请人可以在建筑物所在地公安派出所申请该审批事项。<br><br>五、审批期限：<br><br> 公安派出所自受理门弄号申请之日起7个工作日内提出门弄号编订意见报分局审批；分局应当在7个工作日内经实地勘察后作出审批决定；市公安局一般在7个工作日内作出审批决定。</p>', 5, 0, 1570786067432);
