create table common_problem
(
    id           BIGINT(19) auto_increment comment 'id'
        primary key,
    title        VARCHAR(255) null comment '标题',
    content      TEXT(65535)  null comment '内容',
    jurisdiction BIGINT(19)   null comment '辖区',
    is_visible   BIGINT(19)   null comment '是否可见',
    create_time  BIGINT(19)   null comment '创建时间'
)
    collate = utf8_bin;

