create table jurisdiction_configuration
(
    id                      BIGINT(19) auto_increment
        primary key,
    logo_url                VARCHAR(255)            null,
    is_vehicle_license      VARCHAR(255)            null,
    is_virtual              VARCHAR(255)            null,
    is_parking_restrictions VARCHAR(255)            null,
    phone                   VARCHAR(255)            not null,
    longitude               VARCHAR(255)            null,
    latitude                VARCHAR(255)            null,
    name                    VARCHAR(255)            null,
    jurisdiction            BIGINT(19)              null,
    bike_Icon1              VARCHAR(255)            null comment '电动车图标',
    bike_Icon2              VARCHAR(255)            null comment '助力车图标',
    other_day               BIGINT(19)              null comment '前几天',
    start_time              VARCHAR(20)             null comment '查询开始时间',
    next_day                BIGINT(19)              null comment '后几天',
    end_time                VARCHAR(20)             null comment '查询结束时间',
    stop_marker1            VARCHAR(255)            null comment '停留点（30分钟以内）',
    stop_marker2            VARCHAR(255)            null comment '停留点（30~60分钟以内）',
    stop_marker3            VARCHAR(255)            null comment '停留点（60分钟以外）',
    show_real_line          VARCHAR(10) default '1' null comment '是否显示小尾巴',
    color                   VARCHAR(10)             null comment '小尾巴颜色',
    security_guard          VARCHAR(1000)           null comment '安全守护自定义'
)
    charset = utf8mb4;

