INSERT INTO cyoubike.news (id, title, type, content, is_visible, jurisdiction, create_time, thumb_media_id, url) VALUES (66, '中山所召集辅警开展创全岗前训示', '1', '<p style="text-align: center;"><img src="data:image/jpeg;base64," style="max-width:100%;"><br></p><p style="text-align: left;">为切实做好近期“创全”工作、提高辅警在道路交通管理中的精神面貌和工作责任心，中山所召集全体执勤辅警列队，开展岗前训示。分管领导对近阶段道路整治中的重点工作进行通报，并对辅警在执勤过程中的装备佩戴、语言规范、动作指令等方面进行强调，共同提升道路交通文明，展现良好城市形象。&nbsp;&nbsp;<br></p>', '0', 38, 1570674731894, null, null);
