INSERT INTO cyoubike.news (id, title, type, content, is_visible, jurisdiction, create_time, thumb_media_id, url) VALUES (407, '智慧家居丨2019，中国智能家居市场十大预测', '1', '<section powered-by="xiumi.us" style="max-width: 100%;font-variant-numeric: normal;font-variant-east-asian: normal;letter-spacing: 0.544px;line-height: 27.2px;white-space: normal;widows: 1;background-color: rgb(255, 255, 255);box-sizing: border-box !important;overflow-wrap: break-word !important;"><section style="max-width: 100%;box-sizing: border-box !important;overflow-wrap: break-word !important;"><section style="max-width: 100%;box-sizing: border-box !important;overflow-wrap: break-word !important;"><section powered-by="xiumi.us" style="max-width: 100%;box-sizing: border-box !important;overflow-wrap: break-word !important;"><section style="max-width: 100%;box-sizing: border-box !important;overflow-wrap: break-word !important;"><section style="max-width: 100%;box-sizing: border-box !important;overflow-wrap: break-word !important;"><p style="margin-right: 8px;margin-left: 8px;max-width: 100%;min-height: 1em;box-sizing: border-box !important;overflow-wrap: break-word !important;"><span style="max-width: 100%;font-size: 15px;box-sizing: border-box !important;overflow-wrap: break-word !important;">回顾2018年智能家居市场，爆款产品争相涌现，如智能音箱，智能灯泡，智能插座等。《IDC中国智能家居设备市场季度跟踪报告》显示，<strong style="max-width: 100%;box-sizing: border-box !important;overflow-wrap: break-word !important;">2018年中国智能家居市场出货量预计达到1.5亿台</strong>，同比增长35.9%。产品的智能化升级主要围绕APP客户端、语音助手以及机器学习等方面展开。</span></p><p style="margin-right: 8px;margin-left: 8px;max-width: 100%;min-height: 1em;box-sizing: border-box !important;overflow-wrap: break-word !important;"><br  /></p><p style="margin-right: 8px;margin-left: 8px;max-width: 100%;min-height: 1em;box-sizing: border-box !important;overflow-wrap: break-word !important;"><span style="max-width: 100%;font-size: 15px;box-sizing: border-box !important;overflow-wrap: break-word !important;">2019年，随着智能家居概念的深入以及参与者的丰富，竞争将逐渐从产品扩展到生态，