create table news
(
    id             BIGINT(19) auto_increment comment 'id'
        primary key,
    title          VARCHAR(3000) null comment '新闻标题',
    type           VARCHAR(20)   null comment '新闻类型',
    content        LONGTEXT(max) null comment '新闻内容',
    is_visible     VARCHAR(20)   null comment '是否展示',
    jurisdiction   BIGINT(19)    null comment '辖区',
    create_time    BIGINT(19)    null comment '创建时间',
    thumb_media_id VARCHAR(255)  null comment '公众号文章media_id',
    url            VARCHAR(500)  null comment '公众号文章原文链接'
)
    collate = utf8mb4_unicode_ci;

