create table t_check_point_task
(
    id                BIGINT(19) auto_increment
        primary key,
    task_name         VARCHAR(100) null,
    remark            TEXT(65535)  null,
    create_time       BIGINT(19)   null,
    creator           BIGINT(19)   null,
    update_time       BIGINT(19)   null,
    updater           BIGINT(19)   null,
    check_point_id    BIGINT(19)   null,
    target            VARCHAR(20)  null,
    start_time        VARCHAR(255) null,
    end_time          VARCHAR(255) null,
    is_perform        VARCHAR(255) null,
    upload_img_number VARCHAR(255) null
)
    collate = utf8_bin;

